export const RECURRENCE = {
  FREQUENCY: {
    DAILY: 'DAILY',
    WEEKLY: 'WEEKLY',
    MONTHLY: 'MONTHLY',
    YEARLY: 'YEARLY'
  }
}

export const FORMAT = {
  DATE: 'YYYYMMDD',
  TIME: 'HHmmss'
}

export const URL = {
  YAHOO: 'https://calendar.yahoo.com/',
  GOOGLE: 'https://calendar.google.com/calendar/render',
  OUTLOOK: 'https://outlook.live.com/owa/'
}

import GoogleCalendar from '../GoogleCalendar'
import YahooCalendar from '../YahooCalendar'
import ICalendar from '../ICalendar'
import OutlookCalendar from '../OutlookCalendar'

export default class Calendars {
  static getGoogleCalendarUrl (options) {
    return (new GoogleCalendar(options)).render()
  }

  static getYahooCalendarUrl (options) {
    return (new YahooCalendar(options)).render()
  }

  static getMicrosoftCalendarUrl (options) {
    return (new OutlookCalendar(options)).render()
  }

  static downloadIcs (options) {
    return (new ICalendar(options)).download()
  }
}

const identity = str => str

/**
 * Creates a param string from a flat key-value pair.
 *
 * @param {Object} params
 * @param {String} [delimiter = ';']
 * @param {Function} [transformFn = identity] - transformation function to be
 * applied to each param. If omitted, no transform occurs
 * @returns {String}
 */
export const toParamString = (params, delimiter = ';', transformFn = identity) => {
  const paramString = []

  for (let key in params) {
    if (params.hasOwnProperty(key) && params[key] !== undefined) {
      paramString.push(`${key}=${transformFn(params[key])}`)
    }
  }

  return paramString.join(delimiter)
}

/**
 * Creates a query string from a flat key-value pair.
 *
 * @param {Object} params
 * @returns {String}
 */
export const toQueryString = params => toParamString(params, '&', encodeURIComponent)

/**
 * Creates an ICS param string from a flat key-value pair.
 *
 * @param {Object} params
 * @returns {String}
 */
export const toIcsParamString = params => toParamString(params, ';')

import FileSaver from 'file-saver'
import { toIcsParamString } from './data'
import { formatTime } from './time'
import safariFileSave from './safariFileSave'

/**
 * Removes line breaks and ensures that the string is no
 * longer than maxLength chars (or 75 chars if none specified).
 *
 * @param {String} str - string to sanitize
 * @param {Number} maxLength
 * @returns {String}
 */
export const formatText = (str, maxLength) => {
  if (!str) {
    return ''
  }
  str = str.replace(/\n/g, '\\n')
  str = str.substring(0, maxLength)

  return str
}

/**
 * The name of the file will be the event title with alphanumeric chars with the extension `.ics`.
 *
 * @param {String} icsData
 * @returns {Blob}
 */
export const getBlob = (icsData) => {
  return new Blob([icsData], {
    type: 'application/octet-stream' // TODO: change to text/calendar?
  })
}

/**
 * Transforms given string to be valid file name.
 *
 * @param {String} title
 * @returns {String}
 */
export const getFileName = (title) => {
  if (!title) {
    return 'event.ics'
  }
  return `${title.replace(/[^\w ]/g, '')}.ics`
}

/**
 * Returns a random base 36 hash for iCal UID.
 *
 * @returns {String}
 */
export const getUid = () => {
  return Math.random().toString(36).substr(2)
}

/**
 * Converts the given recurrence options to RFC????
 *
 * @param {*} recurrence
 */
export const getRrule = (recurrence) => {
  return toIcsParamString({
    'FREQ': recurrence.frequency,
    'INTERVAL': recurrence.interval,
    'COUNT': recurrence.count,
    'WKST': recurrence.weekStart,
    'UNTIL': formatTime(recurrence.end),
    'BYDAY': recurrence.weekdays,
    'BYMONTHDAY': recurrence.monthdays
  })
}

/**
 * Downloads the given ics as an iCalendar file.
 *
 * @param {String} title - title of the event
 * @param {String} data - ics data
 */
export const download = (title, data) => {
  const fileName = getFileName(title)
  const isSafari = ~navigator
    .userAgent
    .toLowerCase()
    .indexOf('safari')

  if (isSafari) {
    safariFileSave(data, fileName)
  } else {
    FileSaver.saveAs(getBlob(data), fileName)
  }
}

/**
 * Downloads the given ics. Meant to be used only for Safari.
 *
 * @param {String} data - ics data
 * @param {String} fileName - file name to save, ending in .ics
 */
const safariFileSave = (data, fileName) => {
  const anchor = document.createElement('a')
  const encodedData = encodeURIComponent(data)

  anchor.setAttribute('href', `data:text/calendar;charset=utf-8,${encodedData}`)
  anchor.setAttribute('download', fileName)

  if (document.createEvent) {
    const event = document.createEvent('MouseEvents')

    event.initEvent('click', true, true)
    anchor.dispatchEvent(event)
  } else {
    anchor.click()
  }
}

export default safariFileSave

import moment from 'moment'
import { FORMAT } from '../constants'

/**
 * Computes the duration between two dates.
 *
 * @param {String} startDate
 * @param {String} endDate
 * @returns {String}
 */
export const getHoursDuration = (startDate, endDate) => {
  const start = moment(startDate)
  const end = moment(endDate)
  const diff = end.diff(start)

  return moment
    .utc(diff)
    .format('HHmm')
}

/**
 * Formats the given timestamp.
 *
 * @param {String} time
 * @param {String} format - momentjs format
 * @returns {String}
 */
export const formatTime = (time, format = `${FORMAT.DATE}T${FORMAT.TIME}`) => {
  return moment(time).format(format)
}

/**
 * Returns the current timestamp.
 *
 * @returns {String}
 */
export const getTimeCreated = () => {
  return formatTime(new moment())
}
